                        this.participant.muteAudio();
                        this.audioBtnEl.innerHTML = listIcons.disabledSpeaker;
                        this.audioBtnEl.dataset.touchlabel = Q.getObject("webrtc.participantsPopup.turnOnAudio", tool.text);
                        this.isAudioMuted = true;

